css:
	.print-format table, .print-format tr, 
	.print-format td, .print-format div, .print-format p {
		line-height: 120%;
		vertical-align: middle;
	}
	@media screen {
		.print-format {
			width: 4in;
			padding: 0.25in;
			min-height: 8in;
		}
	}


		.table {
    font-size: inherit;
    margin: 10px 0px;
}

html:
{% if letter_head %}
    {{ letter_head }}
{% endif %}

<p class="text-center" style="margin-bottom: 0.75rem">
	<b>{{ doc.select_print_heading or _("POS Invoice") }}</b><br>
    <b>{{ doc.pos_profile or '' }}</b>

</p>
<p>
	<b>{{ _("Invoice No") }}:</b> {{ doc.name }}<br>
	<b>{{ _("Customer") }}:</b> {{ doc.customer_name }}<br>
	<b>{{ _("Date") }}:</b> {{ doc.get_formatted("posting_date") }}<br>
	<b>{{ _("Time") }}:</b> {{  doc.get_formatted("posting_time") }}<br>
    <b>{{ _("Shop/POS") }}:</b> {{ doc.pos_profile or 'ׄ.' }}<br>

    {% if doc.custom_seller_account %}
            {% set user = frappe.get_doc("User", doc.custom_seller_account) %}
            <b>{{ _("Seller") }}:</b> {{ user.full_name if user and user.full_name else "POS User" }}<br>
    {% endif %}

    {% if doc.is_pos and doc.docstatus == 2 %}
      <b>Invoice Status: </b> Cancelled <br>
    {% else %}

        {% if doc.status == 'Paid' %}
            <b>{{ _("Invoice Status") }}:</b> {{ doc.status or 'ׄ.' }}


            {%- for row in doc.payments -%}

                {% if row.amount > 0 %}
                    * <b>{{ row.mode_of_payment }}</b>
                {% endif %}



            {%- endfor -%}

            <br>
        {% endif %}

    {% endif %}

</p>
<br/>


{% if false %}
    {% if doc.pricing_rules|length >= 1 %}
    <div>

        <b>System Discount Applied</b><br/>

        {% for pricing_rule in doc.pricing_rules %}

        {% set pricing_rule_doc = frappe.get_doc('Pricing Rule', pricing_rule.pricing_rule) %}

            <table class="table table-condensed">
    	<thead>
    		<tr>
    			<th width="50%">{{ _("Rule") }}</th>
    			<th width="50%">{{ _("Amount") }} 

    			{%- if pricing_rule_doc.discount_percentage -%} {{ _(" (%)") }} {% endif %}
    			{%- if pricing_rule_doc.discount_amount -%} {{ _(" (€)") }} {% endif %}</th>

    		</tr>
    	</thead>
    	<tbody>
    	    <tr>
        		<td>{{ pricing_rule_doc.title }}</td>
                <td>
                    {%- if pricing_rule_doc.discount_percentage -%} {{ pricing_rule_doc.discount_percentage }} {% endif %}
                    {%- if pricing_rule_doc.discount_amount -%} {{ pricing_rule_doc.discount_amount }} {% endif %}
                </td>
    		</tr>
        </tbody>

        {% endfor %}
    <div>
    {% endif %}
{% endif %}

<table class="table table-condensed">
	<thead>
		<tr>
			<th width="">{{ _("Item") }}</th>
			<th width="" class="text-right">{{ _("Qty") }}</th>
			<th width="" class="text-right">{{ _("Amount") }}</th>
		</tr>
	</thead>
	<tbody>
		{%- for item in doc.items -%}
		{% set item_code = item.item_code %}


        {% set price_list = 'Standard Selling' %}
        {%- set item_price = frappe.db.get_value('Item Price', {'item_code': item_code, 'price_list': price_list}, 'price_list_rate') %}



{%- set current_date = frappe.utils.nowdate() %}

		<tr>
			<td>
				{%- if item.item_name != item.item_code -%}
					{{ item.item_name }}<br>

					{% if false %}
                        {% if item_price > 1  %}
                            <b>Standard Price: {{ item_price }} </b>
                        {% endif %}
                    {% endif %}
                {%- else -%}
                	{{ item.item_code }}
				{%- endif -%}
				{%- if item.serial_no -%}
					<br><b>{{ _("SR.No") }}:</b>
					{{ item.serial_no | replace("\n", ", ") }}
				{%- endif -%}
				<br/>

				{%- set item_price = frappe.db.get_value("Item Price", {"item_code": item.item_code,"price_list": "Standard Selling"}, "price_list_rate") -%}

			</td>
			<td class="text-right">{{ item.qty }} x {{ item.get_formatted("rate") }}</td>
			<td class="text-right">{{ item.get_formatted("amount") }}</td>
		</tr>
		{%- endfor -%}
	</tbody>
</table>


<div>
    <b>Notes:</b>
    {% if doc.custom_remarks %}

        {{ doc.custom_remarks }}

    {% endif %}


</div>


<table class="table table-condensed no-border">
	<tbody>
		<tr>
			{% if doc.flags.show_inclusive_tax_in_print %}
				<td class="text-right" style="width: 70%">
					{{ _("Total Excl. Tax") }}
				</td>
				<td class="text-left">
					{{ doc.get_formatted("net_total", doc) }}
				</td>
			{% else %}
				<td class="text-right" style="width: 70%">
					{{ _("Total") }}
				</td>
				<td class="text-left">
					{{ doc.get_formatted("total", doc) }}
				</td>
			{% endif %}
		</tr>
		{%- for row in doc.taxes -%}
		  {%- if not row.included_in_print_rate or doc.flags.show_inclusive_tax_in_print -%}
			<tr>
				<td class="text-right" style="width: 70%">
				    {% if '%' in row.description %}
					    {{ row.description }}
					{% else %}
					    {{ row.description }} x {{ row.rate }}%
					{% endif %}
				</td>
				<td class="text-left">
					{{ row.get_formatted("tax_amount", doc) }}
				</td>
			<tr>
		  {%- endif -%}
		{%- endfor -%}

		{%- if doc.discount_amount -%}
		<tr>
			<td class="text-right" style="width: 75%">
				{{ _("Discount") }}
			</td>
			<td class="text-left">
				{{ doc.get_formatted("discount_amount") }}
			</td>
		</tr>
		{%- endif -%}
		<tr>
			<td class="text-right" style="width: 75%">
				<b>{{ _("Grand Total") }}</b>
			</td>
			<td class="text-left">
				{{ doc.get_formatted("grand_total") }}
			</td>
		</tr>
		{%- if doc.rounded_total -%}
		<tr>
			<td class="text-right" style="width: 75%">
				<b>{{ _("Rounded Total") }}</b>
			</td>
			<td class="text-left">
				{{ doc.get_formatted("rounded_total") }}
			</td>
		</tr>
		{%- endif -%}
		{%- for row in doc.payments -%}
			<tr>
				<td class="text-right" style="width: 70%">
				    {{ row.mode_of_payment }}
				</td>
				<td class="text-left">
					{{ row.get_formatted("amount", doc) }}
				</td>
			<tr>
		{%- endfor -%}
		<tr>
			<td class="text-right" style="width: 75%">
				<b>{{ _("Paid Amount") }}</b>
			</td>
			<td class="text-left">
				{{ doc.get_formatted("paid_amount") }}
			</td>
		</tr>
		{%- if doc.change_amount -%}
			<tr>
				<td class="text-right" style="width: 75%">
					<b>{{ _("Change Amount") }}</b>
				</td>
				<td class="text-left">
					{{ doc.get_formatted("change_amount") }}
				</td>
			</tr>
		{%- endif -%}
	</tbody>
</table>




{% if (doc.custom_randomise_factor|int) >= 1 %}

{%- if frappe.get_doc("CP Settings").enable_pos_invoice_questioner_survey -%}

{% set survey_items = frappe.get_all("POS Invoice Questioner Survey Item",filters={"active": 1}, fields=["*"]) %}


{% for item in survey_items %}
<div style="text-align: center;">

<p>- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -</p>


<b>{{item.cta_title}}</b>

<br>

{{item.cta_text}}
<br/>

{%- if item.enable_qr_code -%}
    <br>

    <small>or <br> Scan the QR Code  <br> 

{% set survey_link = "http://cpherbalist.com" %}

{% if doc.custom_survey_reference_code is not none %}
    {% set survey_link = item.cta_link.format(invoice=doc.custom_survey_reference_code) %}
{% else %}
    {% set survey_link = item.cta_link.format(invoice='0x32ab') %}
{% endif %}

<img style="width: 19%;" src="https://staging.cpherbalist.com/api/method/cpherbalist.qr_code.get_qr_jpg?size=180x180&data={{ survey_link }}" />

{%- endif -%}

{% endfor %}


<p>- - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -</p>

</div>



<hr>

{%- endif -%}


{% else %}



{% endif %} 

<p>{{ doc.terms or "" }}</p>
<p class="text-center">{{ _("Thank you, please visit again. ❤️") }}</p>
